import { useNavigate } from 'react-router-dom';
import React from 'react';

const user = JSON.parse(localStorage.getItem('user'));
const today = new Date();
const days = ['Pazar', 'Pazartesi', 'Salƒ±', '√áar≈üamba', 'Per≈üembe', 'Cuma', 'Cumartesi'];
const months = ['Ocak', '≈ûubat', 'Mart', 'Nisan', 'Mayƒ±s', 'Haziran', 'Temmuz', 'Aƒüustos', 'Eyl√ºl', 'Ekim', 'Kasƒ±m', 'Aralƒ±k'];

export default function Dashboard() {
  const navigate = useNavigate();

  return (
    <div className="min-h-screen flex bg-gradient-to-br from-[#ee00ee] to-[#7c1fa0] font-sans transition-colors duration-700 relative overflow-hidden">
      {/* Sidebar */}
      <aside className="w-64 flex flex-col items-center py-10 bg-white/90 shadow-2xl rounded-tr-3xl rounded-br-3xl animate-fade-in-left relative z-10">
        <div className="mb-12">
          <span className="text-3xl font-extrabold text-[#ee00ee] tracking-wide drop-shadow-lg">MindMood</span>
        </div>
        <nav className="flex flex-col gap-6 w-full mt-8">
          <SidebarIcon icon={<span role="img" aria-label="home" className="text-[#ee00ee]">üè†</span>} label="Ana Sayfa" active />
          <SidebarIcon icon={<span role="img" aria-label="smile" className="text-[#ee00ee]">üòä</span>} label="Duygularƒ±m" />
          <SidebarIcon icon={<span role="img" aria-label="notebook" className="text-[#ee00ee]">üìî</span>} label="G√ºnl√ºk" />
          <SidebarIcon icon={<span role="img" aria-label="chart" className="text-[#ee00ee]">üìä</span>} label="ƒ∞statistikler" />
          <SidebarIcon icon={<span role="img" aria-label="meditation" className="text-[#ee00ee]">üßò‚Äç‚ôÇÔ∏è</span>} label="Meditasyon" />
          <SidebarIcon icon={<span role="img" aria-label="settings" className="text-[#ee00ee]">‚öôÔ∏è</span>} label="Ayarlar" />
        </nav>
      </aside>
      {/* Main Content */}
      <main className="flex-1 flex flex-col items-center justify-center px-4 py-10 relative animate-fade-in-up z-10">
        <div className="absolute top-8 right-12 flex items-center gap-4">
          <div className="bg-white/80 px-6 py-2 rounded-full flex items-center gap-2 text-lg shadow text-[#ee00ee] font-semibold animate-fade-in border-2 border-[#ee00ee]">
            {today.getDate()} {months[today.getMonth()]} <span className="font-bold">{days[today.getDay()]}</span>
          </div>
          <button
            onClick={() => {
              localStorage.removeItem('user');
              navigate('/');
            }}
            className="ml-4 p-3 rounded-full bg-[#ee00ee] hover:bg-[#7c1fa0] text-white shadow-lg transition-colors relative group"
            title="√áƒ±kƒ±≈ü Yap"
          >
            <span role="img" aria-label="logout" className="text-2xl">‚Ü©Ô∏è</span>
            <span className="absolute left-1/2 -translate-x-1/2 top-12 bg-[#ee00ee] text-white text-xs rounded px-2 py-1 opacity-0 group-hover:opacity-100 transition-opacity pointer-events-none">√áƒ±kƒ±≈ü Yap</span>
          </button>
        </div>
        {/* Ho≈ü Geldiniz ve avatar */}
        <div className="flex flex-col items-center mb-8 animate-fade-in">
          <div className="w-32 h-32 rounded-full bg-white flex items-center justify-center shadow-2xl mb-4 animate-bounce-slow border-4 border-[#ee00ee]">
            <svg width="80" height="80" viewBox="0 0 24 24" fill="none" stroke="#ee00ee" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><circle cx="12" cy="8" r="4"/><path d="M6 20c0-2 4-3 6-3s6 1 6 3"/></svg>
          </div>
          <h1 className="text-4xl font-extrabold mb-2 tracking-tight text-white drop-shadow">Ho≈ü Geldiniz!</h1>
          <p className="text-lg text-white mb-2">Bug√ºn nasƒ±l hissediyorsunuz?</p>
          <span className="text-[#ee00ee] font-bold">{user?.username || 'Kullanƒ±cƒ±'}</span>
        </div>
        <div className="w-full max-w-xl bg-white/90 rounded-2xl shadow-2xl p-8 flex flex-col items-center mb-8 animate-fade-in-up border-2 border-[#ee00ee]">
          <textarea
            placeholder="Duygularƒ±nƒ±zƒ± buraya yazƒ±n..."
            className="w-full min-h-32 max-h-60 px-6 py-4 rounded-xl bg-white border-2 border-[#ee00ee] text-[#7c1fa0] placeholder-[#ee00ee] focus:outline-none focus:ring-2 focus:ring-[#ee00ee] transition-all mb-4 shadow resize-y overflow-x-hidden"
          />
          <button className="flex items-center gap-2 px-8 py-3 bg-gradient-to-r from-[#ee00ee] to-[#7c1fa0] rounded-xl text-white font-bold shadow-lg hover:scale-105 hover:shadow-2xl hover:from-[#7c1fa0] hover:to-[#ee00ee] transition-all text-lg animate-pulse border-2 border-[#ee00ee]">
            <svg width="20" height="20" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" viewBox="0 0 24 24"><path d="M12 19V6M5 12l7-7 7 7"/></svg>
            Analiz Et
          </button>
        </div>
        <div className="flex gap-8 mt-4">
