import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../lib/supabase';

export default function Register() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      // Supabase ile kullanıcı kaydı
      const { data, error } = await supabase.auth.signUp({
        email: formData.email,
        password: formData.password,
        options: {
          data: {
            username: formData.username
          }
        }
      });

      if (error) throw error;

      if (data?.user) {
        // Kullanıcı bilgilerini users tablosuna ekle
        const { error: userError } = await supabase
          .from('users')
          .insert([
            {
              id: data.user.id,
              username: formData.username,
              email: formData.email
            }
          ]);

        if (userError) throw userError;

        // Kullanıcı bilgilerini localStorage'a kaydet
        localStorage.setItem('user', JSON.stringify({
          id: data.user.id,
          username: formData.username,
          email: formData.email
        }));

        navigate('/dashboard');
      }
    } catch (error) {
      console.error('Kayıt hatası:', error);
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-[#ee00ee] to-[#7c1fa0] p-4">
      <div className="bg-white/90 p-8 rounded-2xl shadow-2xl w-full max-w-md animate-fade-in-up border-2 border-[#ee00ee]">
        <h2 className="text-3xl font-bold text-center mb-8 text-[#ee00ee]">Kayıt Ol</h2>
        
        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-4">
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label className="block text-[#7c1fa0] font-medium mb-2">Kullanıcı Adı</label>
            <input
              type="text"
              name="username"
              value={formData.username}
              onChange={handleChange}
              className="w-full px-4 py-2 rounded-lg border-2 border-[#ee00ee] focus:outline-none focus:ring-2 focus:ring-[#ee00ee]"
              required
            />
          </div>

          <div>
            <label className="block text-[#7c1fa0] font-medium mb-2">Email</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className="w-full px-4 py-2 rounded-lg border-2 border-[#ee00ee] focus:outline-none focus:ring-2 focus:ring-[#ee00ee]"
              required
            />
          </div>

          <div>
            <label className="block text-[#7c1fa0] font-medium mb-2">Şifre</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              className="w-full px-4 py-2 rounded-lg border-2 border-[#ee00ee] focus:outline-none focus:ring-2 focus:ring-[#ee00ee]"
              required
              minLength="6"
            />
          </div>

          <button
            type="submit"
            disabled={loading}
            className={`w-full py-3 rounded-lg text-white font-bold text-lg transition-all ${
              loading 
                ? 'bg-gray-400 cursor-not-allowed' 
                : 'bg-gradient-to-r from-[#ee00ee] to-[#7c1fa0] hover:from-[#7c1fa0] hover:to-[#ee00ee]'
            }`}
          >
            {loading ? 'Kaydediliyor...' : 'Kayıt Ol'}
          </button>
        </form>

        <p className="mt-6 text-center text-[#7c1fa0]">
          Zaten hesabınız var mı?{' '}
          <button
            onClick={() => navigate('/login')}
            className="text-[#ee00ee] font-bold hover:underline"
          >
            Giriş Yap
          </button>
        </p>
      </div>
    </div>
  );
}
