import React from 'react';

const user = JSON.parse(localStorage.getItem('user'));
const today = new Date();
const days = ['Pazar', 'Pazartesi', 'Salƒ±', '√áar≈üamba', 'Per≈üembe', 'Cuma', 'Cumartesi'];
const months = ['Ocak', '≈ûubat', 'Mart', 'Nisan', 'Mayƒ±s', 'Haziran', 'Temmuz', 'Aƒüustos', 'Eyl√ºl', 'Ekim', 'Kasƒ±m', 'Aralƒ±k'];

export default function Dashboard() {
  return (
    <div className="min-h-screen flex bg-gradient-to-br from-[#7f53ac] via-[#647dee] to-[#b721ff] text-[#2d2350] font-sans transition-colors duration-700 relative overflow-hidden">
      {/* Animated Blobs */}
      <AnimatedBlobs />
      {/* Sidebar */}
      <aside className="w-64 flex flex-col items-center py-10 bg-gradient-to-b from-[#7f53ac] to-[#b721ff] shadow-2xl rounded-tr-3xl rounded-br-3xl animate-fade-in-left relative z-10">
        <div className="mb-12">
          <span className="text-3xl font-bold text-white tracking-wide drop-shadow-lg">MindMood</span>
        </div>
        <nav className="flex flex-col gap-6 w-full mt-8">
          <SidebarIcon icon={<span role="img" aria-label="home">üè†</span>} label="Ana Sayfa" active />
          <SidebarIcon icon={<span role="img" aria-label="smile">üòä</span>} label="Duygularƒ±m" />
          <SidebarIcon icon={<span role="img" aria-label="notebook">üìî</span>} label="G√ºnl√ºk" />
          <SidebarIcon icon={<span role="img" aria-label="chart">üìä</span>} label="ƒ∞statistikler" />
          <SidebarIcon icon={<span role="img" aria-label="meditation">üßò‚Äç‚ôÇÔ∏è</span>} label="Meditasyon" />
          <SidebarIcon icon={<span role="img" aria-label="settings">‚öôÔ∏è</span>} label="Ayarlar" />
        </nav>
        <div className="mt-auto w-full flex flex-col items-center">
          <SidebarIcon icon={<span role="img" aria-label="logout">‚Ü©Ô∏è</span>} label="√áƒ±kƒ±≈ü Yap" />
        </div>
      </aside>
      {/* Main Content */}
      <main className="flex-1 flex flex-col items-center justify-center px-4 py-10 relative animate-fade-in-up z-10">
        <div className="absolute top-8 right-12 flex items-center gap-4">
          <div className="bg-gradient-to-r from-[#7f53ac] to-[#b721ff] px-6 py-2 rounded-full flex items-center gap-2 text-lg shadow text-white font-semibold animate-fade-in">
            {today.getDate()} {months[today.getMonth()]} <span className="font-bold">{days[today.getDay()]}</span>
          </div>
        </div>
        {/* Ho≈ü Geldiniz ve avatar */}
        <div className="flex flex-col items-center mb-8 animate-fade-in">
          <div className="w-32 h-32 rounded-full bg-gradient-to-br from-[#b721ff] to-[#7f53ac] flex items-center justify-center shadow-2xl mb-4 animate-bounce-slow">
            <svg width="80" height="80" viewBox="0 0 24 24" fill="none" stroke="#fff" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><circle cx="12" cy="8" r="4"/><path d="M6 20c0-2 4-3 6-3s6 1 6 3"/></svg>
          </div>
          <h1 className="text-4xl font-bold mb-2 tracking-tight text-[#7f53ac] drop-shadow">Ho≈ü Geldiniz!</h1>
          <p className="text-lg text-[#7f53ac] mb-2">Bug√ºn nasƒ±l hissediyorsunuz?</p>
          <span className="text-[#b721ff] font-semibold">{user?.username || 'Kullanƒ±cƒ±'}</span>
        </div>
        <div className="w-full max-w-xl bg-white/90 rounded-2xl shadow-2xl p-8 flex flex-col items-center mb-8 animate-fade-in-up">
          <input
            type="text"
