function SidebarIcon({ icon, label, active }) {
  return (
    <div className={`flex items-center gap-4 px-6 py-3 rounded-2xl cursor-pointer transition-all ${active ? 'bg-white/80 text-[#7f53ac] font-bold shadow-lg' : 'hover:bg-white/40 text-white'}`}>
      <div className={`w-10 h-10 flex items-center justify-center rounded-full ${active ? 'bg-white shadow' : 'bg-white/30'} text-2xl`}>{icon}</div>
      <span className="text-lg font-medium">{label}</span>
    </div>
  );
}

function AnimatedCard({ children, delay = 0, className = '' }) {
  return (
    <div
      className={`rounded-xl p-6 w-48 flex flex-col items-center shadow-lg hover:scale-110 hover:rotate-3 hover:glow-border hover:shadow-2xl transition-transform cursor-pointer animate-fade-in-up animated-gradient ${className}`}
      style={{ animationDelay: `${delay}s` }}
    >
      {children}
    </div>
  );
}

function AnimatedBlobs() {
  return (
    <>
      <svg className="absolute top-[-120px] left-[-120px] w-[400px] h-[400px] opacity-30 z-0 animate-blob-move" viewBox="0 0 400 400" fill="none">
        <ellipse cx="200" cy="200" rx="200" ry="200" fill="#b721ff" />
      </svg>
      <svg className="absolute bottom-[-100px] right-[-100px] w-[300px] h-[300px] opacity-20 z-0 animate-blob-move2" viewBox="0 0 400 400" fill="none">
        <ellipse cx="200" cy="200" rx="200" ry="200" fill="#7f53ac" />
      </svg>
      <style>{`
        .animate-blob-move {
          animation: blobMove 12s ease-in-out infinite alternate;
        }
        .animate-blob-move2 {
          animation: blobMove2 14s ease-in-out infinite alternate;
        }
        @keyframes blobMove {
          0% { transform: scale(1) translate(0,0); }
          50% { transform: scale(1.1) translate(40px, 30px); }
          100% { transform: scale(1) translate(0,0); }
        }
        @keyframes blobMove2 {
          0% { transform: scale(1) translate(0,0); }
          50% { transform: scale(1.15) translate(-30px, -20px); }
          100% { transform: scale(1) translate(0,0); }
        }
      `}</style>
    </>
  );
}
