import { useNavigate } from 'react-router-dom';
import React, { useState } from 'react';

const user = JSON.parse(localStorage.getItem('user'));
const today = new Date();
const days = ['Pazar', 'Pazartesi', 'Salƒ±', '√áar≈üamba', 'Per≈üembe', 'Cuma', 'Cumartesi'];
const months = ['Ocak', '≈ûubat', 'Mart', 'Nisan', 'Mayƒ±s', 'Haziran', 'Temmuz', 'Aƒüustos', 'Eyl√ºl', 'Ekim', 'Kasƒ±m', 'Aralƒ±k'];

export default function Dashboard() {
  const navigate = useNavigate();
  const [settingsOpen, setSettingsOpen] = useState(false);
  const [theme, setTheme] = useState('light');
  const [username, setUsername] = useState(user?.username || '');
  const [password, setPassword] = useState('');
  const [avatar, setAvatar] = useState(null);
  const [avatarPreview, setAvatarPreview] = useState(null);

  // Avatar √∂nizleme
  const handleAvatarChange = (e) => {
    const file = e.target.files[0];
    setAvatar(file);
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => setAvatarPreview(reader.result);
      reader.readAsDataURL(file);
    } else {
      setAvatarPreview(null);
    }
  };

  // Tema toggle
  const handleThemeToggle = () => setTheme(theme === 'light' ? 'dark' : 'light');

  // Kullanƒ±cƒ± adƒ± deƒüi≈ütir (√∂rnek, backend entegrasyonu eklenebilir)
  const handleUsernameSave = () => {
    // API √ßaƒürƒ±sƒ± yapƒ±labilir
    alert('Kullanƒ±cƒ± adƒ± g√ºncellendi: ' + username);
  };

  // ≈ûifre deƒüi≈ütir (√∂rnek, backend entegrasyonu eklenebilir)
  const handlePasswordSave = () => {
    // API √ßaƒürƒ±sƒ± yapƒ±labilir
    alert('≈ûifre g√ºncellendi!');
    setPassword('');
  };

  // Profil fotoƒürafƒ± kaydet (√∂rnek, backend entegrasyonu eklenebilir)
  const handleAvatarSave = () => {
    // API √ßaƒürƒ±sƒ± yapƒ±labilir
    alert('Profil fotoƒürafƒ± g√ºncellendi!');
  };

  // Hesabƒ± sil (√∂rnek, backend entegrasyonu eklenebilir)
  const handleDeleteAccount = () => {
    if (window.confirm('Hesabƒ±nƒ±zƒ± silmek istediƒüinize emin misiniz? Bu i≈ülem geri alƒ±namaz.')) {
      // API √ßaƒürƒ±sƒ± yapƒ±labilir
      alert('Hesabƒ±nƒ±z silindi!');
      localStorage.removeItem('user');
      navigate('/');
    }
  };

  return (
    <div className={`min-h-screen flex bg-gradient-to-br from-[#ee00ee] to-[#7c1fa0] font-sans transition-colors duration-700 relative overflow-hidden ${theme === 'dark' ? 'dark' : ''}`}>
      {/* Sidebar */}
      <aside className="w-64 flex flex-col items-center py-10 bg-white/90 shadow-2xl rounded-tr-3xl rounded-br-3xl animate-fade-in-left relative z-10">
        <div className="mb-12">
          <span className="text-3xl font-extrabold text-[#ee00ee] tracking-wide drop-shadow-lg">MindMood</span>
        </div>
        <nav className="flex flex-col gap-6 w-full mt-8">
          <SidebarIcon icon={<span role="img" aria-label="home" className="text-[#ee00ee]">üè†</span>} label="Ana Sayfa" active />
          <SidebarIcon icon={<span role="img" aria-label="smile" className="text-[#ee00ee]">üòä</span>} label="Duygularƒ±m" />
          <SidebarIcon icon={<span role="img" aria-label="notebook" className="text-[#ee00ee]">üìî</span>} label="G√ºnl√ºk" />
          <SidebarIcon icon={<span role="img" aria-label="chart" className="text-[#ee00ee]">üìä</span>} label="ƒ∞statistikler" />
          <SidebarIcon icon={<span role="img" aria-label="meditation" className="text-[#ee00ee]">üßò‚Äç‚ôÇÔ∏è</span>} label="Meditasyon" />
          <div onClick={() => setSettingsOpen(true)}>
            <SidebarIcon icon={<span role="img" aria-label="settings" className="text-[#ee00ee] cursor-pointer">‚öôÔ∏è</span>} label="Ayarlar" />
          </div>
        </nav>
      </aside>
      {/* Main Content */}
      <main className="flex-1 flex flex-col items-center justify-center px-4 py-10 relative animate-fade-in-up z-10">
        <div className="absolute top-8 right-12 flex items-center gap-4">
          <div className="bg-white/80 px-6 py-2 rounded-full flex items-center gap-2 text-lg shadow text-[#ee00ee] font-semibold animate-fade-in border-2 border-[#ee00ee]">
            {today.getDate()} {months[today.getMonth()]} <span className="font-bold">{days[today.getDay()]}</span>
          </div>
          <button
            onClick={() => {
              localStorage.removeItem('user');
              navigate('/');
            }}
            className="ml-4 p-3 rounded-full bg-[#ee00ee] hover:bg-[#7c1fa0] text-white shadow-lg transition-colors relative group"
            title="√áƒ±kƒ±≈ü Yap"
          >
            <span role="img" aria-label="logout" className="text-2xl">‚Ü©Ô∏è</span>
            <span className="absolute left-1/2 -translate-x-1/2 top-12 bg-[#ee00ee] text-white text-xs rounded px-2 py-1 opacity-0 group-hover:opacity-100 transition-opacity pointer-events-none">√áƒ±kƒ±≈ü Yap</span>
          </button>
        </div>
        {/* Ho≈ü Geldiniz ve avatar */}
        <div className="flex flex-col items-center mb-8 animate-fade-in">
          <div className="w-32 h-32 rounded-full bg-white flex items-center justify-center shadow-2xl mb-4 animate-bounce-slow border-4 border-[#ee00ee]">
            <svg width="80" height="80" viewBox="0 0 24 24" fill="none" stroke="#ee00ee" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><circle cx="12" cy="8" r="4"/><path d="M6 20c0-2 4-3 6-3s6 1 6 3"/></svg>
          </div>
          <h1 className="text-4xl font-extrabold mb-2 tracking-tight text-white drop-shadow">Ho≈ü Geldiniz!</h1>
          <p className="text-lg text-white mb-2">Bug√ºn nasƒ±l hissediyorsunuz?</p>
          <span className="text-[#ee00ee] font-bold">{user?.username || 'Kullanƒ±cƒ±'}</span>
        </div>
        <div className="w-full max-w-xl bg-white/90 rounded-2xl shadow-2xl p-8 flex flex-col items-center mb-8 animate-fade-in-up border-2 border-[#ee00ee]">
          <textarea
            placeholder="Duygularƒ±nƒ±zƒ± buraya yazƒ±n..."
            className="w-full min-h-32 max-h-60 px-6 py-4 rounded-xl bg-white border-2 border-[#ee00ee] text-[#7c1fa0] placeholder-[#ee00ee] focus:outline-none focus:ring-2 focus:ring-[#ee00ee] transition-all mb-4 shadow resize-y overflow-x-hidden"
          />
          <button className="flex items-center gap-2 px-8 py-3 bg-gradient-to-r from-[#ee00ee] to-[#7c1fa0] rounded-xl text-white font-bold shadow-lg hover:scale-105 hover:shadow-2xl hover:from-[#7c1fa0] hover:to-[#ee00ee] transition-all text-lg animate-pulse border-2 border-[#ee00ee]">
            <svg width="20" height="20" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" viewBox="0 0 24 24"><path d="M12 19V6M5 12l7-7 7 7"/></svg>
            Analiz Et
          </button>
        </div>
        <div className="flex gap-8 mt-4">
          <AnimatedCard delay={0} className="bg-white/90 text-[#ee00ee] border-2 border-[#ee00ee]">
            <span className="text-4xl mb-2">üèÖ</span>
            <span className="font-bold text-lg">Rozetler</span>
          </AnimatedCard>
          <AnimatedCard delay={0.15} className="bg-white/90 text-[#ee00ee] border-2 border-[#ee00ee]">
            <span className="text-4xl mb-2">üìà</span>
            <span className="font-bold text-lg">Geli≈üim Haritasƒ±</span>
          </AnimatedCard>
          <AnimatedCard delay={0.3} className="bg-white/90 text-[#ee00ee] border-2 border-[#ee00ee]">
            <span className="text-4xl mb-2">üßò‚Äç‚ôÇÔ∏è</span>
            <span className="font-bold text-lg">Meditasyon</span>
          </AnimatedCard>
        </div>
      </main>
      {/* Ayarlar Paneli */}
      {settingsOpen && (
        <div className="fixed top-0 right-0 h-full w-[380px] bg-white shadow-2xl z-50 animate-fade-in-left border-l-4 border-[#ee00ee] flex flex-col p-8 gap-6">
          <button onClick={() => setSettingsOpen(false)} className="absolute top-4 right-4 text-[#ee00ee] text-2xl font-bold hover:text-[#7c1fa0]">√ó</button>
          <h2 className="text-2xl font-extrabold text-[#ee00ee] mb-4">Ayarlar</h2>
          {/* Tema Se√ßimi */}
          <div className="flex items-center justify-between mb-4">
            <span className="font-semibold text-[#7c1fa0]">Tema</span>
            <button onClick={handleThemeToggle} className={`w-16 h-8 rounded-full flex items-center px-1 transition-colors duration-300 ${theme === 'dark' ? 'bg-[#7c1fa0]' : 'bg-[#ee00ee]/40'}`}> 
              <span className={`w-6 h-6 rounded-full bg-white shadow transform transition-transform duration-300 ${theme === 'dark' ? 'translate-x-8' : ''}`}></span>
            </button>
            <span className="ml-2 text-sm text-[#7c1fa0]">{theme === 'dark' ? 'Koyu' : 'A√ßƒ±k'}</span>
          </div>
          {/* Kullanƒ±cƒ± adƒ± */}
          <div className="mb-4">
            <label className="block text-[#7c1fa0] font-semibold mb-1">Kullanƒ±cƒ± Adƒ±</label>
            <input value={username} onChange={e => setUsername(e.target.value)} className="w-full px-4 py-2 rounded-lg border-2 border-[#ee00ee] focus:outline-none focus:ring-2 focus:ring-[#ee00ee]" />
            <button onClick={handleUsernameSave} className="mt-2 px-4 py-2 bg-[#ee00ee] text-white rounded-lg font-bold hover:bg-[#7c1fa0] transition">Kaydet</button>
          </div>
          {/* ≈ûifre */}
          <div className="mb-4">
            <label className="block text-[#7c1fa0] font-semibold mb-1">Yeni ≈ûifre</label>
            <input type="password" value={password} onChange={e => setPassword(e.target.value)} className="w-full px-4 py-2 rounded-lg border-2 border-[#ee00ee] focus:outline-none focus:ring-2 focus:ring-[#ee00ee]" />
            <button onClick={handlePasswordSave} className="mt-2 px-4 py-2 bg-[#ee00ee] text-white rounded-lg font-bold hover:bg-[#7c1fa0] transition">≈ûifreyi Deƒüi≈ütir</button>
          </div>
          {/* Profil Fotoƒürafƒ± */}
          <div className="mb-4">
            <label className="block text-[#7c1fa0] font-semibold mb-1">Profil Fotoƒürafƒ±</label>
            <input type="file" accept="image/*" onChange={handleAvatarChange} className="mb-2" />
            {avatarPreview && <img src={avatarPreview} alt="Avatar Preview" className="w-20 h-20 rounded-full object-cover border-2 border-[#ee00ee] mx-auto mb-2" />}
            <button onClick={handleAvatarSave} className="px-4 py-2 bg-[#ee00ee] text-white rounded-lg font-bold hover:bg-[#7c1fa0] transition">Fotoƒürafƒ± Kaydet</button>
          </div>
          {/* Hesabƒ± Sil */}
          <div className="mt-8">
            <button onClick={handleDeleteAccount} className="w-full px-4 py-2 bg-red-500 text-white rounded-lg font-bold hover:bg-red-700 transition">Hesabƒ± Sil</button>
          </div>
        </div>
      )}
      <style>{`
        .animate-fade-in { animation: fadeIn 1s ease; }
        .animate-fade-in-up { animation: fadeInUp 1.2s cubic-bezier(.39,.575,.56,1) both; }
        .animate-fade-in-left { animation: fadeInLeft 1.2s cubic-bezier(.39,.575,.56,1) both; }
        .animate-bounce-slow { animation: bounce 2.5s infinite alternate; }
        @keyframes fadeIn { from { opacity: 0; } to { opacity: 1; } }
        @keyframes fadeInUp { from { opacity: 0; transform: translateY(40px); } to { opacity: 1; transform: none; } }
        @keyframes fadeInLeft { from { opacity: 0; transform: translateX(-40px); } to { opacity: 1; transform: none; } }
        @keyframes bounce { 0% { transform: translateY(0); } 100% { transform: translateY(-12px); } }
        .animated-gradient {
          background: white;
          background-size: 1200% 1200%;
          animation: gradientMove 8s ease-in-out infinite;
        }
        @keyframes gradientMove {
          0% {background-position:0% 50%}
          50% {background-position:100% 50%}
          100% {background-position:0% 50%}
        }
        .glow-border {
          box-shadow: 0 0 16px 4px #ee00ee88, 0 0 32px 8px #7c1fa044;
        }
      `}</style>
    </div>
  );
}

function SidebarIcon({ icon, label, active }) {
  return (
    <div className={`flex items-center gap-4 px-6 py-3 rounded-2xl cursor-pointer transition-all border-2 ${active ? 'bg-[#ee00ee] text-white font-bold shadow-lg border-[#ee00ee]' : 'hover:bg-[#ee00ee]/10 text-[#ee00ee] border-transparent'}`}>
      <div className={`w-10 h-10 flex items-center justify-center rounded-full ${active ? 'bg-white shadow text-[#ee00ee]' : 'bg-white/60 text-[#ee00ee]'} text-2xl transition-all`}>{icon}</div>
      <span className="text-lg font-medium">{label}</span>
    </div>
  );
}

function AnimatedCard({ children, delay = 0, className = '' }) {
  return (
    <div
      className={`rounded-xl p-6 w-48 flex flex-col items-center shadow-lg hover:scale-110 hover:rotate-3 hover:glow-border hover:shadow-2xl transition-transform cursor-pointer animate-fade-in-up animated-gradient ${className}`}
      style={{ animationDelay: `${delay}s` }}
    >
      {children}
    </div>
  );
}

function AnimatedBlobs() { return null; }
