import React, { useState } from 'react';
import { Link } from 'react-router-dom';

export default function Login() {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    // Giriş işlemleri burada yapılacak
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  return (
    <section className="min-h-screen bg-gradient-to-r from-indigo-600 to-purple-600 flex items-center justify-center px-4">
      <div className="bg-white/10 backdrop-blur-lg w-full max-w-md p-8 rounded-2xl shadow-2xl transform hover:scale-[1.02] transition-all duration-300">
        <div className="text-center mb-8">
          <h2 className="text-4xl font-bold text-white mb-2">Hoş Geldiniz</h2>
          <p className="text-purple-200">Hesabınıza giriş yapın</p>
        </div>
        
        <form onSubmit={handleSubmit} className="space-y-6">
          <div className="relative">
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className="w-full px-4 py-3 bg-white/5 border border-purple-300/30 rounded-lg text-white placeholder-purple-200 focus:outline-none focus:ring-2 focus:ring-purple-400 focus:border-transparent backdrop-blur-sm transition-all"
              placeholder="E-posta adresiniz"
              required
            />
            <div className="absolute inset-0 rounded-lg bg-gradient-to-r from-purple-400/20 to-indigo-400/20 opacity-0 group-hover:opacity-100 transition-opacity pointer-events-none"></div>
          </div>

          <div className="relative">
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              className="w-full px-4 py-3 bg-white/5 border border-purple-300/30 rounded-lg text-white placeholder-purple-200 focus:outline-none focus:ring-2 focus:ring-purple-400 focus:border-transparent backdrop-blur-sm transition-all"
              placeholder="Şifreniz"
              required
            />
          </div>

          <div className="flex items-center justify-between text-sm">
            <label className="flex items-center text-purple-200 hover:text-white transition-colors cursor-pointer">
              <input type="checkbox" className="mr-2 rounded border-purple-300/30 bg-white/5" />
              Beni hatırla
            </label>
            <a href="#" className="text-purple-200 hover:text-white transition-colors">Şifremi unuttum</a>
          </div>

          <button
            type="submit"
            className="w-full py-3 px-4 bg-gradient-to-r from-purple-500 to-indigo-500 text-white rounded-lg font-semibold hover:from-purple-600 hover:to-indigo-600 transform hover:scale-105 transition-all duration-300 shadow-lg hover:shadow-xl"
          >
            Giriş Yap
          </button>
        </form>

        <div className="mt-8 text-center">
          <p className="text-purple-200">
            Hesabınız yok mu?{' '}
            <Link to="/register" className="text-white font-semibold hover:text-purple-300 transition-colors">
              Kayıt Ol
            </Link>
          </p>
        </div>

        <div className="mt-8 flex items-center justify-center space-x-4">
          <button className="p-2 bg-white/5 rounded-full hover:bg-white/10 transition-all duration-300">
            <span className="text-2xl">🔵</span>
          </button>
          <button className="p-2 bg-white/5 rounded-full hover:bg-white/10 transition-all duration-300">
            <span className="text-2xl">⭕</span>
          </button>
          <button className="p-2 bg-white/5 rounded-full hover:bg-white/10 transition-all duration-300">
            <span className="text-2xl">🔷</span>
          </button>
        </div>
      </div>
    </section>
  );
}
