// Aynı importlar
import { useNavigate } from 'react-router-dom';
import React, { useState } from 'react';

const user = JSON.parse(localStorage.getItem('user'));
const today = new Date();
const days = ['Pazar', 'Pazartesi', 'Salı', 'Çarşamba', 'Perşembe', 'Cuma', 'Cumartesi'];
const months = ['Ocak', 'Şubat', 'Mart', 'Nisan', 'Mayıs', 'Haziran', 'Temmuz', 'Ağustos', 'Eylül', 'Ekim', 'Kasım', 'Aralık'];

export default function Dashboard() {
  const navigate = useNavigate();
  const [settingsOpen, setSettingsOpen] = useState(false);

  return (
    <div className="min-h-screen flex bg-gradient-to-br from-indigo-100 to-purple-100 font-sans transition-colors duration-700 relative overflow-hidden">
      {/* Sidebar */}
      <aside className="w-64 flex flex-col items-center py-10 bg-white/90 shadow-2xl rounded-tr-lg rounded-br-lg animate-fade-in-left relative z-10">
        <div className="mb-12">
          <span className="text-3xl font-extrabold text-indigo-600 tracking-wide drop-shadow-lg">MindMood</span>
        </div>
        <nav className="flex flex-col gap-6 w-full mt-8">
          <SidebarIcon icon="🏠" label="Ana Sayfa" active />
          <SidebarIcon icon="😊" label="Duygularım" />
          <SidebarIcon icon="📔" label="Günlük" />
          <SidebarIcon icon="📊" label="İstatistikler" />
          <SidebarIcon icon="🧘‍♂️" label="Meditasyon" />
          <SidebarIcon icon="⚙️" label="Ayarlar" onClick={() => setSettingsOpen(true)} />
        </nav>
      </aside>

      {/* Main Content */}
      <main className="flex-1 flex flex-col items-center justify-center px-4 py-10 relative animate-fade-in-up z-10">
        <div className="absolute top-8 right-12 flex items-center gap-4">
          <div className="bg-white/80 px-6 py-2 rounded-full flex items-center gap-2 text-lg shadow text-indigo-600 font-semibold animate-fade-in border-2 border-indigo-600">
            {today.getDate()} {months[today.getMonth()]} <span className="font-bold">{days[today.getDay()]}</span>
          </div>
          <button
            onClick={() => {
              localStorage.removeItem('user');
              navigate('/');
            }}
            className="ml-4 p-3 rounded-full bg-gradient-to-r from-indigo-600 to-purple-600 text-white shadow-lg transition-all relative group"
            title="Çıkış Yap"
          >
            <span role="img" aria-label="logout" className="text-2xl">↩️</span>
            <span className="absolute left-1/2 -translate-x-1/2 top-12 bg-indigo-600 text-white text-xs rounded px-2 py-1 opacity-0 group-hover:opacity-100 transition-opacity pointer-events-none">Çıkış Yap</span>
          </button>
        </div>

        {/* Hoş Geldiniz */}
        <div className="flex flex-col items-center mb-8 animate-fade-in">
          <div className="w-32 h-32 rounded-full bg-white flex items-center justify-center shadow-2xl mb-4 animate-bounce-slow border-4 border-indigo-600">
            <svg width="80" height="80" viewBox="0 0 24 24" fill="none" stroke="#6366f1" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><circle cx="12" cy="8" r="4"/><path d="M6 20c0-2 4-3 6-3s6 1 6 3"/></svg>
          </div>
          <h1 className="text-4xl font-extrabold mb-2 tracking-tight text-indigo-600 drop-shadow">Hoş Geldiniz!</h1>
          <p className="text-lg text-indigo-600 mb-2">Bugün nasıl hissediyorsunuz?</p>
          <span className="text-indigo-600 font-bold">{user?.username || 'Kullanıcı'}</span>
        </div>

        {/* Duygu Girişi */}
        <div className="w-full max-w-xl bg-white/90 rounded-lg shadow-2xl p-8 flex flex-col items-center mb-8 animate-fade-in-up border-2 border-indigo-200">
          <textarea
            placeholder="Duygularınızı buraya yazın..."
            className="w-full min-h-32 max-h-60 px-6 py-4 rounded-lg bg-white border-2 border-indigo-200 text-indigo-600 placeholder-indigo-400 focus:outline-none focus:ring-2 focus:ring-indigo-500 transition-all mb-4 shadow resize-y overflow-x-hidden"
          />
          <button className="flex items-center gap-2 px-8 py-3 bg-gradient-to-r from-indigo-600 to-purple-600 rounded-lg text-white font-bold shadow-lg hover:scale-105 hover:shadow-2xl transition-all text-lg border-2 border-indigo-600">
            <svg width="20" height="20" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" viewBox="0 0 24 24"><path d="M12 19V6M5 12l7-7 7 7"/></svg>
            Analiz Et
          </button>
        </div>

        {/* İstatistik Kartları */}
        <div className="flex gap-8 mt-4">
          <AnimatedCard icon="🏅" title="Rozetler" delay={0} />
          <AnimatedCard icon="📈" title="Gelişim Haritası" delay={0.15} />
          <AnimatedCard icon="🧘‍♂️" title="Meditasyon" delay={0.3} />
        </div>
      </main>

      {/* Ayarlar Paneli */}
      {settingsOpen && (
        <div className="fixed top-0 right-0 h-full w-[380px] bg-white shadow-2xl z-50 animate-fade-in-left border-l-4 border-indigo-600 flex flex-col p-8 gap-6 rounded-l-lg">
          <button onClick={() => setSettingsOpen(false)} className="absolute top-4 right-4 text-indigo-600 text-2xl font-bold hover:text-purple-600">×</button>
          {/* Ayar içeriği buraya eklenecek */}
        </div>
      )}
    </div>
  );
}

function SidebarIcon({ icon, label, active, onClick }) {
  return (
    <div
      onClick={onClick}
      className={`flex items-center gap-4 px-6 py-3 rounded-lg cursor-pointer transition-all border-2 ${
        active
          ? 'bg-indigo-600 text-white font-bold shadow-lg border-indigo-600'
          : 'hover:bg-indigo-100 text-indigo-600 border-transparent'
      }`}
    >
      <div className="w-10 h-10 flex items-center justify-center rounded-full bg-gradient-to-r from-indigo-600 to-purple-600 text-white text-2xl">
        {icon}
      </div>
      <span className="text-lg font-medium">{label}</span>
    </div>
  );
}

function AnimatedCard({ icon, title, delay = 0 }) {
  return (
    <div
      className="rounded-lg p-6 w-48 flex flex-col items-center shadow-lg border-2 border-indigo-100 hover:border-indigo-200 transition-transform hover:scale-105 cursor-pointer animate-fade-in-up bg-white"
      style={{ animationDelay: `${delay}s` }}
    >
      <div className="w-14 h-14 flex items-center justify-center rounded-full bg-gradient-to-r from-indigo-600 to-purple-600 text-white text-3xl mb-2">
        {icon}
      </div>
      <span className="font-bold text-lg text-indigo-600">{title}</span>
    </div>
  );
}
