import React from 'react';
export default function Hero() {
  return (
    <section className="bg-gradient-to-r from-indigo-500 to-purple-500 text-white py-20">
      <div className="container mx-auto flex flex-col md:flex-row items-center">
        <div className="md:w-1/2 text-center md:text-left">
          <h1 className="text-4xl font-bold mb-4">Duygularınızı Anlayın</h1>
          <p className="mb-6">Yapay zeka destekli duygu analizi ile duygularınızı daha iyi anlayın ve yönetin.</p>
          <a href="#features" className="inline-block bg-white text-indigo-600 px-6 py-3 rounded-full font-semibold hover:bg-gray-100 transition">Hemen Başla</a>
        </div>
        <div className="md:w-1/2 mt-8 md:mt-0 flex justify-center">
          <img src="/assets/hero-image.png" alt="Duygu Analizi" className="w-3/4" />
        </div>
      </div>
    </section>
  );
}
