import React from 'react';

const user = JSON.parse(localStorage.getItem('user'));
const today = new Date();
const days = ['Pazar', 'Pazartesi', 'Salı', 'Çarşamba', 'Perşembe', 'Cuma', 'Cumartesi'];
const months = ['Ocak', 'Şubat', 'Mart', 'Nisan', 'Mayıs', 'Haziran', 'Temmuz', 'Ağustos', 'Eylül', 'Ekim', 'Kasım', 'Aralık'];

export default function Dashboard() {
  return (
    <div className="min-h-screen flex bg-gradient-to-br from-[#2d006e] via-[#4f1c94] to-[#2d006e] text-white font-sans transition-colors duration-700 relative overflow-hidden">
      {/* Animated Blobs */}
      <AnimatedBlobs />
      {/* Sidebar */}
      <aside className="w-64 flex flex-col items-center py-10 bg-[#2d006e]/90 shadow-2xl rounded-tr-3xl rounded-br-3xl animate-fade-in-left relative z-10">
        <div className="mb-12">
          <span className="text-3xl font-bold text-white tracking-wide drop-shadow-lg">MindMood</span>
        </div>
        <nav className="flex flex-col gap-6 w-full mt-8">
          <SidebarIcon icon={<span role="img" aria-label="home" className="text-white">🏠</span>} label="Ana Sayfa" active />
          <SidebarIcon icon={<span role="img" aria-label="smile" className="text-white">😊</span>} label="Duygularım" />
          <SidebarIcon icon={<span role="img" aria-label="notebook" className="text-white">📔</span>} label="Günlük" />
          <SidebarIcon icon={<span role="img" aria-label="chart" className="text-white">📊</span>} label="İstatistikler" />
          <SidebarIcon icon={<span role="img" aria-label="meditation" className="text-white">🧘‍♂️</span>} label="Meditasyon" />
          <SidebarIcon icon={<span role="img" aria-label="settings" className="text-white">⚙️</span>} label="Ayarlar" />
        </nav>
        <div className="mt-auto w-full flex flex-col items-center">
          <SidebarIcon icon={<span role="img" aria-label="logout" className="text-white">↩️</span>} label="Çıkış Yap" />
        </div>
      </aside>
      {/* Main Content */}
      <main className="flex-1 flex flex-col items-center justify-center px-4 py-10 relative animate-fade-in-up z-10">
        <div className="absolute top-8 right-12 flex items-center gap-4">
          <div className="bg-[#3a2067]/80 px-6 py-2 rounded-full flex items-center gap-2 text-lg shadow text-white font-semibold animate-fade-in">
            {today.getDate()} {months[today.getMonth()]} <span className="font-bold">{days[today.getDay()]}</span>
          </div>
        </div>
        {/* Hoş Geldiniz ve avatar */}
        <div className="flex flex-col items-center mb-8 animate-fade-in">
          <div className="w-32 h-32 rounded-full bg-[#3a2067]/80 flex items-center justify-center shadow-2xl mb-4 animate-bounce-slow">
            <svg width="80" height="80" viewBox="0 0 24 24" fill="none" stroke="#fff" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><circle cx="12" cy="8" r="4"/><path d="M6 20c0-2 4-3 6-3s6 1 6 3"/></svg>
          </div>
          <h1 className="text-4xl font-bold mb-2 tracking-tight text-white drop-shadow">Hoş Geldiniz!</h1>
          <p className="text-lg text-white mb-2">Bugün nasıl hissediyorsunuz?</p>
          <span className="text-white font-semibold">{user?.username || 'Kullanıcı'}</span>
        </div>
        <div className="w-full max-w-xl bg-[#3a2067]/80 rounded-2xl shadow-2xl p-8 flex flex-col items-center mb-8 animate-fade-in-up">
          <input
            type="text"
            placeholder="Duygularınızı buraya yazın..."
            className="w-full px-6 py-4 rounded-xl bg-[#3a2067]/80 border border-[#4f1c94] text-white placeholder-[#bdb2ff] focus:outline-none focus:ring-2 focus:ring-[#4f1c94] transition-all mb-4 shadow"
          />
          <button className="flex items-center gap-2 px-8 py-3 bg-gradient-to-r from-[#4f1c94] to-[#2d006e] rounded-xl text-white font-semibold shadow-lg hover:scale-105 hover:shadow-2xl transition-all text-lg animate-pulse">
            <svg width="20" height="20" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" viewBox="0 0 24 24"><path d="M12 19V6M5 12l7-7 7 7"/></svg>
            Analiz Et
          </button>
        </div>
        <div className="flex gap-8 mt-4">
          <AnimatedCard delay={0} className="bg-[#3a2067]/80 text-white">
            <span className="text-4xl mb-2">🏅</span>
            <span className="font-bold text-lg">Rozetler</span>
          </AnimatedCard>
          <AnimatedCard delay={0.15} className="bg-[#3a2067]/80 text-white">
            <span className="text-4xl mb-2">📈</span>
            <span className="font-bold text-lg">Gelişim Haritası</span>
          </AnimatedCard>
          <AnimatedCard delay={0.3} className="bg-[#3a2067]/80 text-white">
            <span className="text-4xl mb-2">🧘‍♂️</span>
            <span className="font-bold text-lg">Meditasyon</span>
          </AnimatedCard>
        </div>
      </main>
      <style>{`
        .animate-fade-in { animation: fadeIn 1s ease; }
        .animate-fade-in-up { animation: fadeInUp 1.2s cubic-bezier(.39,.575,.56,1) both; }
        .animate-fade-in-left { animation: fadeInLeft 1.2s cubic-bezier(.39,.575,.56,1) both; }
        .animate-bounce-slow { animation: bounce 2.5s infinite alternate; }
        @keyframes fadeIn { from { opacity: 0; } to { opacity: 1; } }
        @keyframes fadeInUp { from { opacity: 0; transform: translateY(40px); } to { opacity: 1; transform: none; } }
        @keyframes fadeInLeft { from { opacity: 0; transform: translateX(-40px); } to { opacity: 1; transform: none; } }
        @keyframes bounce { 0% { transform: translateY(0); } 100% { transform: translateY(-12px); } }
        .animated-gradient {
          background: #3a2067cc;
          background-size: 1200% 1200%;
          animation: gradientMove 8s ease-in-out infinite;
        }
        @keyframes gradientMove {
          0% {background-position:0% 50%}
          50% {background-position:100% 50%}
          100% {background-position:0% 50%}
        }
        .glow-border {
          box-shadow: 0 0 16px 4px #4f1c9488, 0 0 32px 8px #2d006e44;
        }
      `}</style>
    </div>
  );
}

function SidebarIcon({ icon, label, active }) {
  return (
    <div className={`flex items-center gap-4 px-6 py-3 rounded-2xl cursor-pointer transition-all ${active ? 'bg-[#3a2067]/80 text-white font-bold shadow-lg' : 'hover:bg-[#3a2067]/60 text-white'}`}>
      <div className={`w-10 h-10 flex items-center justify-center rounded-full ${active ? 'bg-[#4f1c94] shadow' : 'bg-[#3a2067]/60'} text-2xl`}>{icon}</div>
      <span className="text-lg font-medium">{label}</span>
    </div>
  );
}

function AnimatedCard({ children, delay = 0, className = '' }) {
  return (
    <div
      className={`rounded-xl p-6 w-48 flex flex-col items-center shadow-lg hover:scale-110 hover:rotate-3 hover:glow-border hover:shadow-2xl transition-transform cursor-pointer animate-fade-in-up animated-gradient ${className}`}
      style={{ animationDelay: `${delay}s` }}
    >
      {children}
    </div>
  );
}

function AnimatedBlobs() {
  return null;
}
