import { useNavigate } from 'react-router-dom';
import React, { useState } from 'react';

const user = JSON.parse(localStorage.getItem('user'));
const today = new Date();
const days = ['Pazar', 'Pazartesi', 'Salƒ±', '√áar≈üamba', 'Per≈üembe', 'Cuma', 'Cumartesi'];
const months = ['Ocak', '≈ûubat', 'Mart', 'Nisan', 'Mayƒ±s', 'Haziran', 'Temmuz', 'Aƒüustos', 'Eyl√ºl', 'Ekim', 'Kasƒ±m', 'Aralƒ±k'];

export default function Dashboard() {
  const navigate = useNavigate();
  const [settingsOpen, setSettingsOpen] = useState(false);
  const [activeTab, setActiveTab] = useState('diary'); // G√ºnl√ºk varsayƒ±lan aktif
  const [diaryContent, setDiaryContent] = useState('');

  const handleDiarySubmit = () => {
    // TODO: G√ºnl√ºk i√ßeriƒüini kaydet
    console.log('G√ºnl√ºk kaydedildi:', diaryContent);
    setDiaryContent('');
  };

  return (
    <div className="min-h-screen flex bg-[#f6f6fa] font-sans transition-colors duration-700">
      {/* Sidebar */}
      <aside className="w-72 min-h-screen flex flex-col items-center py-10 bg-gradient-to-b from-[#b983ff] to-[#7f53ac] shadow-2xl">
        <div className="mb-12 flex flex-col items-center">
          <span className="text-3xl font-extrabold text-white tracking-wide drop-shadow-lg flex items-center gap-2"><span role='img' aria-label='kalp'>üíú</span>Duygu Analizi</span>
        </div>
        <nav className="flex flex-col gap-4 w-full mt-4 px-4">
          <SidebarIcon icon={<span role="img" aria-label="home">üè†</span>} label="Ana Sayfa" active={activeTab === 'home'} onClick={() => setActiveTab('home')} />
          <SidebarIcon icon={<span role="img" aria-label="smile">üòä</span>} label="Duygularƒ±m" active={activeTab === 'emotions'} onClick={() => setActiveTab('emotions')} />
          <SidebarIcon icon={<span role="img" aria-label="notebook">üìî</span>} label="G√ºnl√ºk" active={activeTab === 'diary'} onClick={() => setActiveTab('diary')} />
          <SidebarIcon icon={<span role="img" aria-label="chart">üìä</span>} label="Analiz" active={activeTab === 'stats'} onClick={() => setActiveTab('stats')} />
          <SidebarIcon icon={<span role="img" aria-label="settings">‚öôÔ∏è</span>} label="Ayarlar" active={activeTab === 'settings'} onClick={() => setActiveTab('settings')} />
          <SidebarIcon icon={<span role="img" aria-label="logout">‚Ü©Ô∏è</span>} label="√áƒ±kƒ±≈ü Yap" onClick={() => {localStorage.removeItem('user'); navigate('/');}} />
        </nav>
      </aside>
      {/* Main Content */}
      <main className="flex-1 flex flex-col bg-[#f6f6fa] min-h-screen">
        {/* √úst Bar */}
        <div className="w-full flex justify-end items-center px-12 pt-8">
          <div className="flex items-center gap-4 bg-white rounded-2xl shadow-lg px-8 py-4 border border-[#e0d7f3]">
            <span className="text-[#7c1fa0] font-semibold text-lg flex items-center gap-2"><span role='img' aria-label='calendar'>üìÖ</span>{today.getDate()} {months[today.getMonth()]} {today.getFullYear()} {days[today.getDay()]}</span>
            <div className="w-10 h-10 rounded-full bg-[#f6f6fa] flex items-center justify-center border-2 border-[#7c1fa0] ml-4">
              <svg width="28" height="28" viewBox="0 0 24 24" fill="none" stroke="#7c1fa0" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><circle cx="12" cy="8" r="4"/><path d="M6 20c0-2 4-3 6-3s6 1 6 3"/></svg>
            </div>
            <span className="ml-2 text-[#7c1fa0] font-bold text-lg">{user?.username || 'a'}</span>
          </div>
        </div>
        {/* ƒ∞√ßerik */}
        {activeTab === 'diary' && (
          <div className="flex flex-col items-center w-full mt-8">
            <h2 className="text-4xl font-bold text-[#7c1fa0] mb-6 w-full max-w-5xl">Duygu G√ºnl√ºƒü√ºm</h2>
            <div className="w-full max-w-5xl bg-white rounded-2xl shadow-xl p-8 border border-[#e0d7f3] flex flex-col">
              <textarea
                value={diaryContent}
                onChange={(e) => setDiaryContent(e.target.value)}
                placeholder="Bug√ºn neler hissettiniz?"
                className="w-full min-h-[180px] max-h-[300px] px-6 py-4 rounded-xl bg-white border border-[#e0d7f3] text-[#7c1fa0] placeholder-[#b983ff] focus:outline-none focus:ring-2 focus:ring-[#b983ff] transition-all mb-6 shadow-sm resize-y overflow-x-hidden text-lg"
              />
              <button
                onClick={handleDiarySubmit}
                className="w-40 h-12 bg-gradient-to-r from-[#b983ff] to-[#7c1fa0] rounded-lg text-white font-bold shadow-md hover:scale-105 hover:shadow-xl transition-all text-lg border-none self-start"
              >
                Kaydet
              </button>
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

function SidebarIcon({ icon, label, active, onClick }) {
  return (
    <button
      onClick={onClick}
      className={`flex items-center gap-3 px-6 py-3 rounded-lg font-semibold text-lg transition-all mb-1 ${active ? 'bg-[#00e6d0] text-white shadow-lg' : 'bg-white/10 text-white hover:bg-[#b983ff]/30'} ${label === '√áƒ±kƒ±≈ü Yap' ? 'mt-8 bg-white/20 text-white hover:bg-[#ff5e62]/60' : ''}`}
      style={{outline: active ? '2px solid #00e6d0' : 'none'}}
    >
      <span className="text-2xl">{icon}</span>
      <span>{label}</span>
    </button>
  );
}
